mapper.select:
SELECT COUNT(*) FROM ${format}_asset WHERE repository_id = #{repositoryId}
     AND kind = #{kind}
     AND ${filter};

SELECT * FROM ${format}_asset WHERE repository_id = #{repositoryId}
     AND asset_id > #{continuationToken}
     AND kind = #{kind}
     AND ${filter}
ORDER BY asset_id LIMIT #{limit};

SELECT * FROM ${format}_asset
WHERE repository_id IN

    #{repositoryId}

   AND asset_id > #{continuationToken}
ORDER BY asset_id LIMIT #{limit};

mapper.delete:
DELETE FROM ${format}_asset WHERE
     asset_id IN (SELECT asset_id FROM ${format}_asset WHERE 
    repository_id = #{repositoryId}
     LIMIT #{limit});

DELETE FROM ${format}_asset WHERE repository_id = #{repositoryId} AND path IN

  #{path}
;
